#include <ostream>
#include <iostream>
#include <vector>
#include <algorithm>
#include <ios>
#include <istream>
#include <math.h>
#include <iomanip>
#include <string.h>
using namespace std;
typedef long long ll;
typedef string str;
int main(void){
    ios::sync_with_stdio(0);cin.tie(0);cout.tie(0);
    ll n, ans = 0;
    str s, check = ""; cin >> n >> s;
    for(int i = 0;i < n - 1;i++) {
        if (s.substr(i, 2) == "ah") {
            check = "a";
            ll res = 0;
            for(;i < n;i++) 
                if (s.substr(i, 1) == check) res++, check = check=="a"?"h":"a";
                else break;
            ans = max(res, ans);
            i--;
        }
        else if (s.substr(i, 2) == "ha") {
            check = "h";
            ll res = 0;
            for (; i < n; i++) {
                if (s.substr(i, 1) == check) res++, check = (check == "a" ? "h" : "a");
                else break;
            }
            ans = max(res, ans);
            i--;
        }
    }
    cout << ans << '\n';
    return 0;
}
