value=input()
v=value.split("e")

if int(v[1])>26:
    result = str(round(float(v[0])*10**(int(v[1])-27),3))+" Octillion"
elif int(v[1])>23:
    result = str(round(float(v[0])*10**(int(v[1])-24),3))+" Septillion"
elif int(v[1])>20:
    result = str(round(float(v[0])*10**(int(v[1])-21),3))+" Sextillion"
elif int(v[1])>17:
    result = str(round(float(v[0])*10**(int(v[1])-18),3))+" Quintillion"
elif int(v[1])>14:
    result = str(round(float(v[0])*10**(int(v[1])-15),3))+" Quadrillion"
elif int(v[1])>11:
    result = str(round(float(v[0])*10**(int(v[1])-12),3))+" Trillion"
elif int(v[1])>8:
    result = str(round(float(v[0])*10**(int(v[1])-9),3))+" Billion"
elif int(v[1])>5:
    result = str(round(float(v[0])*10**(int(v[1])-6),3))+" Million"
else:
    result = str(eval(value))
print(result)

