public class Program
{	
	public static void Main()
	{
        int n = int.Parse(Console.ReadLine());
        for (int i = 0; i < n; i++)
        {
            string aCouple = Console.ReadLine();
            new CoupleModel (aCouple).ShowInfo(); 
        }
	}
}

public class CoupleModel
{
	public string He; 
	public string She;
	public int ShortestPersonLen;
	public List<AComboModel> ComboList;	
	public int LongestCommon;
	public List<AComboModel> Finalists;

	public CoupleModel(string both)
	{
		var Ray = both.Split(' ');
		He = Ray[0];
		She = Ray[2];
		ShortestPersonLen = He.Length;
		if (She.Length < ShortestPersonLen) ShortestPersonLen = She.Length;		

		ComboList = new List<AComboModel>();

		FindCommonSubsStringList(He.ToLower(), She.ToLower());
		FindCommonSubsStringList(She.ToLower(), He.ToLower());
		FindLongestCommon();

		FindFinalists();
	}

	public string GetSub(string word, int firstPos, int lastPos)
	{
		int lengOfSub = lastPos - firstPos + 1;
		string span = word.Substring(firstPos, lengOfSub);
		return span;
	}


	public void FindLongestCommon()
	{
		if (ComboList.Count == 0) 
		{
			LongestCommon = 0;
		}
		else
		{
			LongestCommon = this.ComboList.Max(x => x.CommonLen);	
		}
	}

	public void FindFinalists()
	{
		Finalists = new List<AComboModel>();
		foreach (var combo in ComboList)
		{
			if (combo.CommonLen == LongestCommon)
			{
				Finalists.Add(combo);
			}
		}
		Finalists = Finalists.OrderBy(o=>o.CoupleNameProper).ToList();		
	}

	public void FindCommonSubsStringList(string AAA, string BBB) // Person AAA and Person BBB, sometimes He and She, sometimes She and He
	{
		int lenAAA = AAA.Length;
		int lenBBB = BBB.Length;
		for (int startAAA = 0 ; startAAA < lenAAA ; startAAA++)
		{
			for (int endAAA =  startAAA ; endAAA < lenAAA ; endAAA++)
			{
				for (int startBBB = 0 ; startBBB < lenBBB ; startBBB++)
				{
					for (int endBBB =  startBBB ; endBBB < lenBBB ; endBBB++)
					{
						string subAAA = GetSub(AAA, startAAA, endAAA);
						string subBBB = GetSub(BBB, startBBB, endBBB);
						if (subAAA == subBBB) 
						{
							string commonString = subAAA;
							int commonLen = commonString.Length;

							string leftPart  = GetSub(AAA, 0                   , endAAA - commonLen);
							string rightPart = GetSub(BBB, startBBB + commonLen, lenBBB - 1);

							string cn = leftPart + commonString + rightPart;
							bool isSingleNameSameAsCoupleName = AAA == cn || BBB == cn;
							bool isMinimumLen = cn.Length >= ShortestPersonLen;
							bool somethingFromEachPerson = leftPart.Length > 0 &&  + rightPart.Length > 0;

							bool meetsAllReqs = isMinimumLen && somethingFromEachPerson && isSingleNameSameAsCoupleName == false; //TODO

							if (meetsAllReqs)
							{
								AComboModel temp = new AComboModel(leftPart, commonString, rightPart);
								ComboList.Add(temp);
							}
						}
					}
				}
			}
		}
	}

	public void ShowInfo()
	{ 
		var str = He + " plus " + She + " = " ;
		Console.Write(str);

		string ans = "";
		foreach (AComboModel aCombo in Finalists)
		{
			ans += aCombo.GetCombo() + " ";
		}
		if (Finalists.Count == 0)
		{
			ans = "NONE";
		}
		Console.WriteLine(ans.Trim());
	}

	public class AComboModel
	{
		public string Lef;
		public string Rig;
		public string Common;
		public int CommonLen;
		public string CoupleNameProper;

		public AComboModel(string l, string com, string r)
		{
			Lef = l;
			Common = com;
			Rig = r;
			CommonLen = com.Length;
			MakeItProper();
		}

		public void MakeItProper()
		{
			CoupleNameProper = Lef + Common + Rig;
			CoupleNameProper = char.ToUpper(CoupleNameProper[0]) + CoupleNameProper.Substring(1);
		}

		public string GetCombo()
		{
			string str =  CoupleNameProper;
			string str2 = char.ToUpper(str[0]) + str.Substring(1);
			return str2;
		}
	}
}
