import java.util.LinkedList;
import java.util.List;
import java.util.Scanner;

public class Solution {
	static Node smallest = new Node(0, 1);
	static Node biggest = new Node(1, 0);
	static Node root = new Node(1, 1);

	static List<Node> list = new LinkedList<>();
	static StringBuilder sb = new StringBuilder();

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();
		in.nextLine();

		for (int i = 0; i < N; i++) {
			String line = in.nextLine();

			// Path to Rational Number
			if (line.charAt(0) == 'L' || line.charAt(0) == 'R') {
				P2R(line);
			}

			// Rational Number to Path
			else {
				R2P(line);
			}
		}

	}

	// Path to Rational Num
	static void P2R(String line) {
		list.clear();
		list.add(smallest);
		list.add(root);
		list.add(biggest);
		sb.setLength(0);

		for (char c : line.toCharArray()) {
			if (c == 'L') {
				list.remove(2);
				Node n1 = list.get(0);
				Node n2 = list.get(1);
				list.add(1, new Node(n1.a + n2.a, n1.b + n2.b));
			}
			// R
			else {
				Node n1 = list.get(1);
				Node n2 = list.get(2);
				list.add(2, new Node(n1.a + n2.a, n1.b + n2.b));
				list.remove(0);
			}
		}
		Node n = list.get(1);
		System.out.println(n);
	}

	// Rational Num to Path
	static void R2P(String line) {
		list.clear();
		list.add(smallest);
		list.add(root);
		list.add(biggest);
		sb.setLength(0);

		String[] ss = line.split("/");

		int a = Integer.parseInt(ss[0]);
		int b = Integer.parseInt(ss[1]);
		Node nod = new Node(a, b);
		findPath(nod);
		System.out.println(sb);
	}

	private static void findPath(Node nod) {
		// cmp middle of the list with a/b
		int res = list.get(1).compare(nod);
		// list middle > nod
		if (res > 0) {
			// insert L of middle
			list.remove(2);
			Node n1 = list.get(0);
			Node n2 = list.get(1);
			list.add(1, new Node(n1.a + n2.a, n1.b + n2.b));
			sb.append("L");
		}
		// list middle < nod
		else if (res < 0) {
			// insert R of middle
			Node n1 = list.get(1);
			Node n2 = list.get(2);
			list.add(2, new Node(n1.a + n2.a, n1.b + n2.b));
			list.remove(0);
			sb.append("R");
		}
		// list middle == nod
		else {
			return;
		}

		findPath(nod);
	}

}

// rational number
class Node {
	long a, b;

	public Node(long a, long b) {
		this.a = a;
		this.b = b;
	}

	public int compare(Node nod) {
		long res = (this.a * nod.b - nod.a * this.b);
		if (res > 0) {
			return 1;
		} else if (res < 0) {
			return -1;
		} else {
			return 0;
		}
	}

	@Override
	public String toString() {
		return a + "/" + b;
	}
}
