use 5.020;
use warnings;

sub range_check {
    $_[0] < $_[1] || $_[0] > $_[2] and
        die "$_[0] out of range [$_[1],${_[2]}[\n";
}

my ($N,$M,$X) = <> =~ /\d+/g;
range_check $N,    0, 100000;
range_check    $M, 0, 100000;
range_check $N+$M, 2, 100000;

my @hs = map [ <> =~ /\d+/g ], 1..$N;
sub lcg {
    my $res = $X;
    $X = (1103515245 * $X + 12345) % 2**31;
    return $res;
}
for (1..$M) { push @hs, [ lcg(), lcg() ] }
@hs = sort { $a->[0] <=> $b->[0] } @hs;

my @cross;
my $best = 2**32;

sub dist {
    abs( $hs[$_[1]][0] - $hs[$_[0]][0] ) + abs( $hs[$_[1]][1] - $hs[$_[0]][1] )
}

sub bruteforce {
    my ($l,$r) = @_;
    for my $a ($l..$r-2) {
        for my $b ($a+1..$r-1) {
            my $d = dist( $a, $b );
            $d < $best and $best = $d;
        }
    }
    @cross[$l..$r-1] = sort { $hs[$a][1] <=> $hs[$b][1] } $l..$r-1;
}

sub frobnicate {
    my ($l,$m,$r,$s) = @_;
    my ($i,$j) = ($l,$m);
    my @tmp;
    while ($i < $m && $j < $r) {
        if ($hs[$cross[$i]][1] <= $hs[$cross[$j]][1]) { push @tmp, $cross[$i++] }
        else { push @tmp, $cross[$j++] }
    }
    while ($i < $m) { push @tmp, $cross[$i++] }
    while ($j < $r) { push @tmp, $cross[$j++] }
    grep abs( $hs[$_][0] - $s ) < $best, @cross[$l..$r-1] = @tmp;
}

sub interestingest {
    my ($l,$r) = @_;
    if ($r - $l <= 3) { bruteforce( $l, $r ) }
    else {
        my $m = int( ($l + $r) / 2 );
        my $strength = $hs[$m][0];
        interestingest( $l, $m );
        interestingest( $m, $r );
        my @doubt = frobnicate( $l, $m, $r, $strength );
        for my $a (0..$#doubt) {
            for my $b ($a+1..$a+5) {
                last if $b > $#doubt;
                my $d = dist( $doubt[$a], $doubt[$b] );
                $d < $best and $best = $d;
            }
        }
    }
}

interestingest( 0, $N+$M );
say $best;
