import heapq
from collections.abc import Iterator

Team = tuple[str, tuple[str, ...]]

def ordered_teams(players: list[str], k: int) -> Iterator[Team]:
    m = len(players)
    q = [("", tuple(), 0)]
    while q:
        name, squad, i = heapq.heappop(q)
        if len(name) == k:
            yield name, squad
        else:
            players_needed = k - len(name)
            for j in range(i, m - players_needed + 1):
                heapq.heappush(
                    q,
                    (f"{name}{players[j][0]}", (*squad, players[j]), j + 1),
                )

def ordered_teamings(players: list[str], num_teams: int, team_size: int) -> Iterator[str]:
    expected_size = num_teams * team_size
    teams = list(ordered_teams(players, team_size))
    m = len(teams)
    q = [("", tuple(), 0)]
    while q:
        teaming_str, teaming, i = heapq.heappop(q)
        if len(teaming) == num_teams:
            teamed_players = set(p for _, squad in teaming for p in squad)
            if len(teamed_players) == expected_size:
                yield teaming_str.removesuffix(",")
        else:
            teams_needed = num_teams - len(teaming)
            for j in range(i, m - teams_needed + 1):
                heapq.heappush(
                    q,
                    (f"{teaming_str}{teams[j][0]},", (*teaming, teams[j]), j + 1)
                )

n = int(input())
k = int(input())
m = int(input())
players = sorted(input() for _ in range(m))

for teaming in ordered_teamings(players, n, k):
    print(teaming)
