import java.util.*;
class Solution {

    public static String compute(int L, int div, int PX, int PY, String moves) {
        char[] movesChar = moves.toCharArray();
        for (int i = 0; i < movesChar.length; i++) {
            char move = movesChar[i];
            if (move == 'R') {
                if (PY >= div && PY <= 2 * div - 1) {
                    PX = (PX == (L - 1) ? 0 : PX + 1);
                    continue;
                }
                PX = (PX == (2 * div - 1) ? div : PX + 1);
            }
            if (move == 'L') {
                if (PY >= div && PY <= 2 * div - 1) {
                    PX = (PX == 0 ? L - 1 : PX - 1);
                    continue;
                }
                PX = (PX == div ? (2 * div - 1) : PX - 1);
            }
            if (move == 'D') {
                if (PX >= div && PX <= 2 * div - 1) {
                    PY = (PY == (L - 1) ? 0 : PY + 1);
                    continue;
                }
                PY = (PY == (2 * div - 1) ? div : PY + 1);
            }
            if (move == 'U') {
                if (PX >= div && PX <= 2 * div - 1) {
                    PY = (PY == 0 ? (L - 1) : PY - 1);
                    continue;
                }
                PY = (PY == div ? (2 * div - 1) : PY - 1);
            }
        }


        // Write an action using System.out.println()
        // To debug: System.err.println("Debug messages...");
        return (PX + " " + PY);

    }

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int L = in.nextInt();
        int div = L / 3;

        int PX = in.nextInt();
        int PY = in.nextInt();
        String moves = in.next();
//        int L = 9;
//        int div = L / 3;
//
//        int PX = 5;
//        int PY = 1;
//
//
//        String moves = "DDDDDDDDDD";

        System.out.println(compute(L, div, PX, PY, moves));

    }

}
