def frequent_char(string):
    char_count = {}
    for char in string:
        if char.isalpha() and char.lower() in char_count:
            char_count[char.lower()] += 1
        elif char.isalpha():
            char_count[char.lower()] = 1
    most_frequent_char = max(char_count, key=char_count.get)
    return (most_frequent_char, char_count[most_frequent_char])

input_string = input()
result, count = frequent_char(input_string)
print(result, count)
