data.solution:
word = input()
guess = ' '.join('_'*len(w) for w in word.split())
chars = input().split()

def draw(errors):
    print('+--+')
    if errors == 0:
        print('|')
    else:
        print('|  o')
    if errors < 4:
        print(('|', '|', '|  |', '| /|')[errors])
    else:
        print('| /|\\')
    print('|\\', end='')
    if errors > 4:
        print(('/', '/ \\')[min(errors-5,1)])
    else:
        print('')

def addletter(c, word, guess):
    res = ''
    for i in range(len(word)):
        if word[i].lower() == c:
            res += word[i]
        else :
            res += guess[i]
    return res        

errors = 0
for c in chars:        
    new = addletter(c, word, guess)
    if new == guess :
        errors += 1
        if errors == 6:            
            break
    guess = new
    if guess == word:
        break

draw(errors)
print(guess)
