import java.awt.Point;
import java.util.ArrayList;
import java.util.Collections;
import java.util.Comparator;
import java.util.HashSet;
import java.util.Iterator;
import java.util.LinkedList;
import java.util.List;
import java.util.Scanner;
import java.util.Set;

public class Solution {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int M = in.nextInt();
		int S = in.nextInt();

		Graph graph = new Graph(M, S);

		for (int j = 0; j < M; j++) {
			int x = in.nextInt();
			int y = in.nextInt();
			graph.addNode(j, x, y);
		}
		process(graph, M, S);
		in.close();
	}

	private static void process(Graph graph, int m, int s) {
		graph.createLinks();
		List<Link> mst = graph.findMinSpinningTree(s);
		Link last = mst.get(mst.size() - 1);
		System.out.printf("%.2f\n", Math.sqrt(last.dist));
	}
}

class Graph {
	static int linkId = 0;
	int nodeCount;
	int satellite;
	List<Integer> adj[]; // an array of List
	List<Node> nodeList; // list of stations (Nodes)
	List<Link> linkList; // list of links (distances)

	public Graph(int nodeCount, int s) {
		this.nodeCount = nodeCount;
		this.satellite = s;
		adj = new LinkedList[nodeCount];
		for (int i = 0; i < nodeCount; ++i) {
			adj[i] = new LinkedList();
		}
		nodeList = new ArrayList<>();
		linkList = new ArrayList<>();
	}

	public List<Link> findMinSpinningTree(int s) {
		Set<Link> tmpSet = new HashSet<>();
		List<Link> mstList = new ArrayList<>();
		for (int i = 0; i < linkList.size(); i++) {
			Link ll = linkList.get(i);
			if (!tmpSet.contains(ll)) {
				tmpSet.add(ll);
				if (isCycle(tmpSet)) {
					tmpSet.remove(ll);
				} else {
					mstList.add(ll);
					int treeCount = countTrees();
					if (treeCount <= s) {
						break;
					}
				}
			}
		} // for linkList
		return mstList;
	}

	int countTrees() {
		boolean visited[] = new boolean[nodeCount];
		int res = 0;
		for (int i = 0; i < nodeCount; ++i) {
			if (visited[i] == false) {
				countTreeUtil(i, visited);
				res++;
			}
		}
		return res;
	}

	void countTreeUtil(int v, boolean visited[]) {
		visited[v] = true;
		Iterator<Integer> i = adj[v].listIterator();
		while (i.hasNext()) {
			int n = i.next();
			if (!visited[n]) {
				countTreeUtil(n, visited);
			}
		}
	}

	public boolean isCycle(Set<Link> llSet) {
		boolean result = false;
		for (int i = 0; i < nodeCount; i++) {
			adj[i] = new LinkedList<>();
		}
		for (Link ll : llSet) {
			adj[ll.n1.id].add(ll.n2.id);
			adj[ll.n2.id].add(ll.n1.id);
		}

		boolean[] visited = new boolean[nodeCount];
		for (int i = 0; i < nodeCount; i++) {
			if (visited[i] == false) {
				if (isCycleUtil(adj, i, visited, -1)) {
					return true;
				}
			}
		}
		return result;
	}

	boolean isCycleUtil(List<Integer>[] adjList, int currVertex, boolean[] visited, int parent) {
		visited[currVertex] = true;
		for (int i = 0; i < adjList[currVertex].size(); i++) {
			int vertex = adjList[currVertex].get(i);
			if (vertex != parent) {
				if (visited[vertex]) {
					return true;
				} else {
					if (isCycleUtil(adjList, vertex, visited, currVertex)) {
						return true;
					}
				}
			}
		}
		return false;
	}

	// v, w - two nodes making an edge
	public void addEdge(int v, int w) {
		adj[v].add(w);
		adj[w].add(v);
	}

	public void addNode(int id, int x, int y) {
		nodeList.add(new Node(id, x, y));
	}

	public void createLinks() {
		for (int i = 0; i < nodeCount - 1; i++) {
			for (int j = i + 1; j < nodeCount; j++) {
				Node s1 = nodeList.get(i);
				Node s2 = nodeList.get(j);
				int d = dist(s1.p, s2.p);
				Link ll = new Link(linkId++, d, s1, s2);
				linkList.add(ll);
			}
		}

		Collections.sort(linkList, new Comparator<Link>() {
			@Override
			public int compare(Link o1, Link o2) {
				return Integer.compare(o1.dist, o2.dist);
			}
		});
	}

	private static int dist(Point p1, Point p2) {
		int dx = p1.x - p2.x;
		int dy = p1.y - p2.y;
		return dx * dx + dy * dy;
	}
}

class Node {
	int id;
	Point p; // x,y

	public Node(int id, int x, int y) {
		this.id = id;
		this.p = new Point(x, y);
	}

	@Override
	public String toString() {
		return String.valueOf(id);
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + id;
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (!(obj instanceof Node))
			return false;
		Node other = (Node) obj;
		if (id != other.id)
			return false;
		return true;
	}
}

class Link {
	int id;
	int dist;
	Node n1, n2;

	public Link(int hash, int dist, Node n1, Node n2) {
		this.dist = dist;
		this.n1 = n1;
		this.n2 = n2;
		this.id = hash;
	}

	public boolean hasNodes(int id1, int id2) {
		return n1.id == id1 && n2.id == id2;
	}

	@Override
	public String toString() {
		return "Link [id=" + id + ", dist=" + dist + ", n1=" + n1 + ", n2=" + n2 + "]";
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + id;
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (!(obj instanceof Link))
			return false;
		Link other = (Link) obj;
		if (id != other.id)
			return false;
		return true;
	}
}
