import java.util.Scanner;

public class Solution {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();
		if (in.hasNextLine()) {
			in.nextLine();
		}
		for (int i = 0; i < N; i++) {
			String s = in.nextLine();
			System.out.println(counting(s));
		}
	}

	private static int counting(String s) {
		int sum = 0;
		for (;;) {
			s = trim2(s);
			if (s.length() >= 3) {
				sum++;
				s = s.substring(3);
			}
			if (s.length() < 3) {
				break;
			}
		}
		s = trim2(s);
		if (s.length() > 0) {
			sum++;
		}
		return sum;
	}

	private static String trim2(String s) {
		while (s.startsWith(".")) {
			s = s.substring(1);
		}
		while (s.endsWith(".")) {
			s = s.substring(0, s.length() - 1);
		}
		return s;
	}

}
