{
    static void Main(string[] args)
    {
        string s = Console.ReadLine();
        string[] data = s.Split(' ');

        int len = data.Length;
        int mid = data.Length %2;
        string result = "";
        if(mid ==1)
        {
            result = data[len/2];
        }
        else
        {

            result = data[len/2-1]+data[len/2];
        }

