using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;


public class Program
{
    public static void Main()
    {
        string sp = " ";
        int n = int.Parse(Console.ReadLine());
        for (int i = 0; i < n; i++)
        {
            string phrase = Console.ReadLine();
            var wordsArray  = phrase.Split(' ');

            int arrayLength = wordsArray.Length;
            int middleIndex = wordsArray.Length / 2;

            if(arrayLength % 2 == 0)
            {
                string ans = "";
                for (int index = 0 ; index < middleIndex ; index++ )
                {
                    ans = ans + wordsArray[index] + " ";
                }
                Console.WriteLine(ans.Trim());
            }
            else 
            {
                int lastIndex = wordsArray.Length - 1;
                string ans = wordsArray[0] + sp + wordsArray[middleIndex] + sp + wordsArray[lastIndex];
                Console.WriteLine(ans);
            }
        }
    }
}
