n = int(input())
for files in range(n):
    file = input()

    res = ""
    for i in range(len(file)):
        if file[i].isdigit():
            res += str(int(file[i]) * len(file))
        elif file[i].isalpha():
            res += str(ord(file[i]) % len(file))
        else:
            res += str(i + len(file))
    print(res)
