using System;using System.Linq;using System.IO;using System.Text;using System.Collections;using System.Collections.Generic;//using System.Threading;
//https://puzzlemaker.discoveryeducation.com/word-search/
//https://www.insider.com/most-famous-animals-of-all-time-2019-10
//https://www.loc.gov/rr/print/list/057_pral.html
//https://astronism.fandom.com/f/p/4400000000000122697

public class Program
{
	public static char[,] chRay;
    public static int SIZE;
	public static int[] upDownMoves =    new int[] { -1, -1, 0, 1, 1,  1,  0, -1 }; // starting at 12 oclock,
	public static int[] leftRightMoves = new int[] {  0 , 1, 1, 1, 0, -1, -1, -1 }; // starting at 12 oclock,
	public static List<WordFound> WordsFound;
	public static void Main()
	{   
        int size = int.Parse(Console.ReadLine());
        SIZE = size; 
        chRay = new char[SIZE, SIZE];
        for (int lin = 0; lin < SIZE; lin++)
        {
            string ROW = Console.ReadLine();
            ROW = ROW.ToLower();
            for (int pos = 0 ; pos < SIZE ; pos++)
            {  
                chRay[lin,pos] = ROW[pos];
            }
        }
        string clues = Console.ReadLine();

		var cluesArray = clues.Trim().ToLower().Split(' ');
		WordsFound =  new List<WordFound>();

		foreach (var wtf in cluesArray)//wtf = word to find
		{
			char firstCharInWord = wtf[0];
			for (int lin = 0; lin < SIZE; lin++)
			{
				for (int pos = 0; pos < SIZE; pos++)
				{
					char ch = chRay[lin,pos];
					if (ch == firstCharInWord)
					{
						Point startingPt = new Point(lin, pos);
						CheckRestOfWord(wtf, startingPt);
					}
				}
			}
		}

		foreach (var wordFound in WordsFound)
		{
			wordFound.UpperCaseThem();
		}
		Print2DArrayFINAL(chRay);
	}

	public static void CheckRestOfWord(string word, Point point)
	{
		int len = word.Length;
		int biggestIndex = len - 1;

		for (int whichDirection = 0 ; whichDirection < 8 ; whichDirection++) // try the 8 different directions
		{
			int udMove = upDownMoves[whichDirection];
			int lrMove = leftRightMoves[whichDirection];

			for (int whichLetter = 0 ;  whichLetter <= biggestIndex ; whichLetter++)//look at each letter of word
			{
				char letterInWord = word[whichLetter];
				char charInGrid = '~';
				int tempLin = point.LIN + (udMove * whichLetter);
				int tempPos = point.POS + (lrMove * whichLetter);
				bool b1 = tempLin > -1; bool b2 = tempLin < SIZE;
				bool b3 = tempPos > -1; bool b4 = tempPos < SIZE; 
				bool onGrid = b1 && b2 && b3 && b4;

				if (onGrid == true) 
				{
					charInGrid = chRay[tempLin,tempPos];
				}

				if (letterInWord != charInGrid)
				{
					break;
				}
				if (letterInWord == charInGrid && whichLetter == biggestIndex) // have reached all the way to the last letter
				{
					WordsFound.Add(new WordFound(word, point, whichDirection));
				}		
			}
		}
	}

	public static void Print2DArrayFINAL(char[,] matrix)
    {
        for (int i = 0; i < matrix.GetLength(0); i++)
        {
			string temp = "";
            for (int j = 0; j < matrix.GetLength(1); j++)
            {
				char ch = matrix[i,j]; 
                if (Char.IsUpper(ch))
				{
					temp = temp + ch;
				}
				else
				{
					temp = temp + " ";
				}	
            }
            Console.WriteLine(temp);
        }
    }

	public class Point
	{
		public int LIN; public int POS;

		public Point(int lin, int pos)
		{
			LIN = lin; POS = pos;
		}
	}

	public class WordFound
	{
		public string WordItself;
		public Point StartingPoint;
		public int Direction;
		public int Length;

		public WordFound(string w, Point p, int d)
		{
			WordItself = w;
			StartingPoint = p;
			Direction = d;
			Length = WordItself.Length;
		}

		public void UpperCaseThem()
		{			
			int udMove =  upDownMoves[Direction];
			int lrMove =  leftRightMoves[Direction];

			for (int i = 0 ; i < Length ; i++)
			{
				char ch = chRay[StartingPoint.LIN + i * udMove, StartingPoint.POS + i * lrMove];
				ch 	= Char.ToUpper(ch);
				chRay[StartingPoint.LIN + i * udMove, StartingPoint.POS + i * lrMove] = ch;
			}	
		}
	}
}
