import math
import sys

def distance(a, a1):
    x,y = a
    x1,y1 = a1
    return math.sqrt((x-x1)**2+(y-y1)**2)

n = int(input())
d = {}

for _ in range(n):
    x,y,a = input().split()
    d[(int(x),int(y))] = int(a)

m = int(input())

for _ in range(m):
    x,y = map(int,input().split())
    nn = {}

    for animal in d:
        dist = distance(animal, (x,y))
        nn[dist] = d[animal]

    cat = 0
    dog = 0
    i = 0
    for key in sorted(nn)[:3]:
        animal = nn[key]
        if animal == 1:
            dog += 1
        else:
            cat += 1
        i+=1
    if cat > dog:
        print('cat')
    else:
        print('dog')
