from itertools import product

n=int(input())
dessin=[]
for _ in range(n):
    dessin.append(input())
longueur=len(dessin[-1])
lettres=set()

def résultat(x,y):
    while True:
        if y==longueur-2:
            a=dessin[x][y+1]
            lettres.add(a)
            return a
        if dessin[x][y]=="|":
            x+=1
            continue
        if dessin[x][y]=="'":
            y+=1
            continue
        if dessin[x][y]=="+":
            return "not("+résultat(x,y+1)+")"
        if x+1<n and dessin[x+1][y] in "|'":
            return "not("+résultat(x+1,y)+") or ("+résultat(x,y+1)+")"
        y+=1

formule=résultat(0,0)
lettres="".join(sorted(lettres))
cas=[]

for l in product((False,True),repeat=len(lettres)):
    temp=formule
    for i in range(len(lettres)):
        temp=temp.replace(lettres[i],str(l[i]))
    if not eval(temp):
        cas.append(l)
if cas:
    for l in cas:
        ch=[]
        for i in range(len(lettres)):
            ch+=[lettres[i],str(l[i])]
        print(" ".join(ch))
else:
    print("TAUTOLOGY")

