#include <iostream>
using namespace std;

int main() {
    int n, threshold;
    cin >> n;
    int temp[n];
    for (int i = 0; i < n; i++) {
        cin >> temp[i];
    }
    cin >> threshold;

    int count = 0;
    for (int i = 0; i < n; i++) {
        int maxTemp = temp[i];
        for (int j = i; j < n; j++) {
            if (temp[j] > maxTemp) {
                maxTemp = temp[j];
            }
            if (maxTemp >= threshold) {
                count++;
            }
        }
    }

    cout << count << endl;
    return 0;
}
