text = input()

answer = ""

for char in text:
    number = ord(char.lower())

    if ord("a") <= number <= ord("z"):
        number -= ord("a")
        number = 25 - number

        if char.isupper():
            answer += chr(number + ord("a")).upper()

        else:
            answer += chr(number + ord("a"))

    elif char.isdigit():
        number = int(char)

        answer += str(9-number)

    elif char in "()[]}{":
        answer += {"(":")",")":"(","[":"]","]":"[","{":"}","}":"{"}[char]

    else:
        answer += char

print(answer)
