import math
#Zenos Dichotomy Paradox
def zenos_paradox(archer_position, target_position, steps):
    current = archer_position
    for step in range(steps):
        current = (current + target_position) / 2
    return(current)

def question_2(a,b,c):
    answer = zenos_paradox(a,b,c)
    return(answer)

print(math.ceil(question_2(int(input()),int(input()),int(input()))))
