class Judge {
	static int fight(char c1, char c2) {
		if (c1 == c2) return 0

		if (c1 == 'C' && c2 == 'P') return 1
		else if (c1 == 'P' && c2 == 'C') return -1
		else if (c1 == 'P' && c2 == 'R') return 1
		else if (c1 == 'R' && c2 == 'P') return -1
		else if (c1 == 'R' && c2 == 'L') return 1
		else if (c1 == 'L' && c2 == 'R') return -1
		else if (c1 == 'L' && c2 == 'S') return 1
		else if (c1 == 'S' && c2 == 'L') return -1
		else if (c1 == 'S' && c2 == 'C') return 1
		else if (c1 == 'C' && c2 == 'S') return -1
		else if (c1 == 'C' && c2 == 'L') return 1
		else if (c1 == 'L' && c2 == 'C') return -1
		else if (c1 == 'L' && c2 == 'P') return 1
		else if (c1 == 'P' && c2 == 'L') return -1
		else if (c1 == 'P' && c2 == 'S') return 1
		else if (c1 == 'S' && c2 == 'P') return -1
		else if (c1 == 'S' && c2 == 'R') return 1
		else if (c1 == 'R' && c2 == 'S') return -1
		else if (c1 == 'R' && c2 == 'C') return 1
		else if (c1 == 'C' && c2 == 'R') return -1
		return 0
	}
}
