class Box {
	int x, y
	char sign = 0
	private Set<Character> set = new HashSet<>()

	public Box(int x, int y) {
		this.x = x
		this.y = y
	}

	public Box(int x, int y, char c) {
		this.x = x
		this.y = y
		this.sign = c
	}

	public void confirmWinner() {
		List<Character> winners = new ArrayList(set)

		if (winners.size() == 1) this.sign = winners.get(0)
		else if (winners.size() > 1) {
			int result = Judge.fight(winners.get(0), winners.get(1))
			if (result > 0)	this.sign = winners.get(0)
			else if (result < 0) this.sign = winners.get(1)
		}
		set.clear()
	}

	public boolean isEmpty() {
		return set.size() == 0 || this.sign == 0
	}

	public void addWinner(char v2) {
		set.add(v2)
	}

	@Override
	public String toString() {
		return "Box [x=" + x + ", y=" + y + ", sign=" + sign + ", set=" + set + "]"
	}

}
