private static void showGrids(String s, int w, int h) {
//	System.err << s + "\n"

	StringBuilder sb = new StringBuilder()
	int st = 0
	int ed = w
	for (int y = 0; y < h; y++) {
		sb.append(s.substring(st, ed))
		sb.append("\n")
		st += w
		ed += w
	}
	System.out.print(sb.toString())
}

private String process(Box[][] arr, int w, int h, int n, Set<String> hashSet) {
	Map<Integer, String> map = new HashMap()
	String hash0 = hashCode(arr, w, h)
	map.put(0, hash0)
	hashSet.add(hash0)

	for (int k = 1;; k++) {
		Box[][] arr1 = new Box[w][h]
		for (int y = 0; y < h; y++) {
			for (int x = 0; x < w; x++) {
				arr1[x][y] = new Box(x, y)
			}
		}

		for (int y = 0; y < h; y++) {
			for (int x = 0; x < w - 1; x++) {
				int result = Judge.fight(arr[x][y].sign, arr[x + 1][y].sign)
				switch (result) {
					case 1:
						arr[x + 1][y].addWinner(arr[x][y].sign)
						break
					case -1:
						arr[x][y].addWinner(arr[x + 1][y].sign)
						break
					case 0:
						break
				}
			}
		}

		for (int x = 0; x < w; x++) {
			for (int y = 0; y < h - 1; y++) {
				int result = Judge.fight(arr[x][y].sign, arr[x][y + 1].sign)
				switch (result) {
					case 1:
						arr[x][y + 1].addWinner(arr[x][y].sign)
						break
					case -1:
						arr[x][y].addWinner(arr[x][y + 1].sign)
						break
					case 0:
						break
				}
			}
		}

		for (int y = 0; y < h; y++) {
			for (int x = 0; x < w; x++) {
				arr[x][y].confirmWinner()
			}
		}

		// debug
//		showArr(arr, w, h, k);


		String hash = hashCode(arr, w, h)
		boolean unique = hashSet.add(hash)

		if (k == n) return hash

		map.put(k, hash)

		int st = 0;
		int ed = 0;
		int cLen = 0;

		int ind = 0;
		if (!unique) {
			Iterator it = hashSet.iterator()
			while (it.hasNext()) {
				String s = it.next()

				if (s.equals(hash)) {
					st = ind
					ed = k
					cLen = ed - st

					// wanted day is n
					int iterateCount = (n - st) / cLen
					int st1 = st + (iterateCount * cLen)
					int dif1 = n - st1

					int wantedId = st + dif1
					return map.get(wantedId)
				}
				ind++
			}
		}
	}
}

private static String hashCode(Box[][] arr, int w, int h) {
	StringBuilder sb = new StringBuilder()
	for (int y = 0; y < h; y++) {
		for (int x = 0; x < w; x++) {
			sb.append(arr[x][y].sign)
		}
	}
	return sb.toString()
}

private static void showArr(Box[][] arr, int w, int h, int k) {
	StringBuilder sb = new StringBuilder()
	for (int y = 0; y < h; y++) {
		for (int x = 0; x < w; x++) {
			sb.append(arr[x][y].sign)
		}
		sb.append("\n")
	}
	System.err.println(k)
	System.err.println(sb.toString())
}

