class Node:
    def __init__(self, name, path):
        self.name = name
        self.path = path
        self.children = []
PATH, f, n = input(), input().split(","), int(input())
hidden, direc = "-a" in f, "-d" in f
depth = float("inf")
if any(flag.startswith("-L") for flag in f):
    _flag = next(flag for flag in f if flag.startswith("-L"))
    flag = _flag.split()
    if len(flag) == 2 and flag[1].isnumeric() and int(flag[1]) > 0: depth = int(flag[1])  
Nodes = {".": Node(".", ".")}
for _ in range(n):
    path = input().split("/")
    current = Nodes["."]
    for k, name in enumerate(path):
        if name != "." and name[0] == ".":
            if not hidden: break
        p = "/".join(path[:k+1])
        if p not in Nodes:
            Nodes[p] = Node(name, p)
            current.children.append(Nodes[p])
        current = Nodes[p]
nb_nodes, nb_files = -1, 0
def display(node, full, d, filler, first, last=False):
    global nb_nodes, nb_files
    if d > depth: return
    nb_nodes += 1
    path = node.path if full else node.name
    if last: print(filler + "`-- "*(d>0) + path)
    else: print(filler + "|-- "*(d>0) + path)
    _C = [c for c in node.children if c.children] if direc else node.children
    C = sorted(_C, key=lambda x: x.name.upper() if x.name[0] != "." else x.name[1:].upper())
    for k, c in enumerate(C):
        display(c, False, d+1, filler + ("" if first else "    " if last else "|   "), False, c is C[-1])
    nb_files += C == []
_PATH = PATH if PATH.startswith(".") else "./" + PATH
if _PATH not in Nodes or not Nodes[_PATH].children:
    print(PATH, "[error opening dir]")
    nb_nodes = 0
else: display(Nodes[_PATH], PATH.startswith("."), 0, "", True)
print()
if direc: print(f"{nb_nodes} director{'ies' if nb_nodes != 1 else 'y'}")
else: print(f"{nb_nodes - nb_files} director{'ies' if nb_nodes - nb_files != 1 else 'y'}, {nb_files} file{'s' if nb_files != 1 else ''}")
