n, nb, g = [int(i) for i in input().split()]
maturity = 2
death = 4

if n<2:
    for i in range(n):
        nb += nb * g
    print(nb)
else:
    r = [nb]
    for i in range(n):
        if i > (maturity - 1):
            r.append(sum(r[i- (maturity - 1):i + 1])*g)
            if i > (death - 2):
               r[i- (death - 1)] = 0  
        else:
           r.append(sum(r)*g)
    print(sum(r))
