import sys
import math

width = int(input())
nlinks = int(input())
links=[]
for i in range(nlinks):
    links += [ list(map(int,input().split(" ") )) ]

# symetrize from links
mapp_b  = [[ 0 if i!=j else 1 for i in range(width) ] for j in range(width)]

for i,j in links:
    mapp_b[i][j] = 1
    mapp_b[j][i] = 1

def SetColumnOR(mat,i,j):
    linked = False
    for k in range(len(mat[i])):
        if mat[j][k] and mat[i][k] :
            linked = True
            break
    if not linked:
        return False
    for k in range(len(mat[i])):
        mat[i][k] = 1 if mat[j][k] or mat[i][k] else 0
        mat[j][k] = mat[i][k]
    mat[i] = mat[j]
    return True

def Booleanize(matrix):
    list_done = []
    for i in range(width):
        if i in list_done:continue
        for j in range(i,width):
            if SetColumnOR(matrix,i,j):
                list_done += [j]

Booleanize(mapp_b)

listAnswer = []
listCounted = []
for r in mapp_b:
    ans = []
    for i,c in enumerate(r):
        if c and (i not in listCounted):
            listCounted += [ i ]
            ans += [i]
    if ans: listAnswer += [ ans ]

lp = list(map(len,listAnswer))
lp.sort()
lp.reverse()

distrib=dict(zip(list(set(lp)),[0]*len(set(lp))))

for li in listAnswer:
    distrib[len(li)]=distrib[len(li)]+1

ret=[ (k,v) for k,v in distrib.items() ]
ret.sort()
ret.reverse()

for i in ret:
    print(" ".join(map(str,i)))
