import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }

        List<String> employees = new ArrayList<String>(N);
        long         nbBits    = Integer.toString( N - 1 , 2 ).length();

        System.err.println("N              : " + N );
        System.err.println("Number of bits : " + nbBits );

        for (int i = 0; i < N; i++) {
            String EMPLOYEE = in.nextLine();
            employees.add( EMPLOYEE );
        }

        String EMAIL        = in.nextLine();
        String MARQUER_BITS = "";       
        for(int i = 0 ; i < EMAIL.length() ; i++ ) {
            char c = EMAIL.charAt(i);
            if( c != ' ' ) continue;

            if( c == ' ' ) {
                String bit = "0";
                if( i + 1 < EMAIL.length() ) {
                    char next = EMAIL.charAt(i+1);
                    if( next == ' ' ) {
                        bit = "1";
                        i++;
                    }
                }
                MARQUER_BITS += bit;
                if( MARQUER_BITS.length() == nbBits ) {
                    break;
                }
           }
        }

        int ID = Integer.parseInt( MARQUER_BITS , 2 );
        System.err.println("BITS           : " + MARQUER_BITS );
        System.err.println("ID             : " + ID );

        System.out.println( employees.get( ID ) );
    }
}
