package main

import "os"
import "bufio"
import "fmt"
import "strings"

type Word struct {
    x int
    y int
    str string
}

func main() {

    scanner := bufio.NewScanner(os.Stdin)
    scanner.Buffer(make([]byte, 1000000), 1000000)

    scanner.Scan()
    H1 := Word{0, 0, scanner.Text()}
    scanner.Scan()
    H2 := Word{0, 0, scanner.Text()}
    scanner.Scan()
    V1 := Word{0, 0, scanner.Text()}
    scanner.Scan()
    V2 := Word{0, 0, scanner.Text()}

    solutions := [][]Word{}

     for i1h1, L1h1 := range(H1.str){
         for i1v1, L1v1 := range(V1.str){
             if L1h1 == L1v1 {
                V1.x = i1h1
                V1.y = -i1v1
                for i2h1, L2h1 := range(H1.str){ 
                    if (i2h1 < i1h1 -1 || i2h1 > i1h1 +1) { 
                        for i1v2, L1v2 := range(V2.str){
                            if L2h1 == L1v2 {
                                V2.x = i2h1
                                V2.y = -i1v2
                                for h2y := max(V1.y, V2.y) ; h2y < min(V1.y + len(V1.str) ,V2.y + len(V2.str)) ; h2y ++ {
                                    for h2x := 1 + max(V1.x, V2.x) - len(H2.str) ; h2x <= min(V1.x, V2.x); h2x ++ {
                                        if (h2y > 1 || h2y < -1){
                                            if H2.str[V1.x - h2x] == V1.str[-V1.y + h2y] && H2.str[V2.x - h2x] == V2.str[-V2.y + h2y] {
                                                H2.x = h2x
                                                H2.y = h2y
                                                solutions = append(solutions, []Word{H1, H2, V1, V2})
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                } 
             }
         }
     }

    if (len(solutions) == 1){

        sol := solutions[0]
        minX, minY, maxX, maxY  := 0, 0, 0, 0

        for i,word := range(sol){

            if (i <2){
                if word.x + len(word.str) > maxX {
                    maxX = word.x + len(word.str)
                }
                if word.y > maxY {
                    maxY = word.y
                }
            } else {
                if word.x  > maxX {
                    maxX = word.x
                }
                if word.y + len(word.str)> maxY {
                    maxY = word.y + len(word.str)
                }
            }

            if word.x < minX {
                minX = word.x
            }
             if word.y < minY {
                minY = word.y
            }
        }

        for i,word := range(sol){
          word.x -= minX
          word.y -= minY
          sol[i] = word
        }

        maxX -= minX
        maxY -= minY

        grid := make([][]string, maxY)

        for i := 0; i < maxY; i++{
           grid[i] =  make([]string, maxX)
           for j := 0; j < maxX; j++ {
              grid[i][j] = "." 
           }
        }

        for i,word := range(sol){
          if i < 2 {
              for j,char := range(word.str){
                  grid[word.y][word.x + j] = string(char)
              }
          } else {
              for j,char := range(word.str){
                  grid[word.y +j][word.x] = string(char)
              } 
          }
        }

        for i := 0; i < maxY; i++{
           fmt.Println(strings.Join(grid[i], ""))
        }

    } else {
        fmt.Println(len(solutions))
    } 
}

func min (a int, b int) int{
    if a > b {
        return b
    }
    return a
}

func max (a int, b int) int{
    if a > b {
        return a
    }
    return b
}
