import java.util.Arrays;
import java.util.Scanner;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    private static final String CRANE_ARM = "_";
    private static final String CRANE_FULCRUM = "/\\";
    private static final String CRANE_COUNTERWEIGHT = "?";

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String craneAsciiRepresentation = in.nextLine();
        String[] splitCrane = craneAsciiRepresentation.split(CRANE_ARM);
        int massPosition = -1, massWeight = 0, counterWeightPos = -1;
        int fulcrumPos = Arrays.asList(splitCrane).indexOf(CRANE_FULCRUM);
        for (int j = 0; j < splitCrane.length; j++) {
            final String[] item = splitCrane[j].split("");
            if (Arrays.stream(item).allMatch(c -> !c.isBlank() && Character.isDigit(c.charAt(0)))) {
                massPosition = j;
                massWeight = Integer.parseInt(splitCrane[j]);
            } else if (splitCrane[j].equals(CRANE_COUNTERWEIGHT)) {
                counterWeightPos = j;
            }
        }

        final int massPositionRelative = Math.abs(massPosition - fulcrumPos),
                counterWeightPositionRelative = Math.abs(counterWeightPos - fulcrumPos);

        System.err.printf("%s * %s / %s %n", massWeight, massPositionRelative, counterWeightPositionRelative);

        System.out.println(massWeight * massPositionRelative / counterWeightPositionRelative);

    }
}

