#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main()
{
    int x;
    cin >> x; cin.ignore();
    vector<string> c;
    for (int i = 0; i < x; i++) {
        string numbers;
        getline(cin, numbers);

        string a;
        for(auto i : numbers)
            if(isdigit(i))
                a+=i;

        c.push_back(a);
    }
    int y;
    cin >> y; cin.ignore();
    for (int i = 0; i < y; i++) {
        string check;
        getline(cin, check);
        bool l = true;
        for(auto a:check)
            if(!isdigit(a))
            {
                l=false;
                break;
            }


        if(find(c.begin(), c.end(), check) == c.end() && l)
        {
            cout << "Weirdo Stalker!\n";
        }
        else
        cout << "You Know This Weirdo Stalker!\n";
    }
}
