data.solution:
n, words = int(input()), []
for i in range(n):
    words.append(input())

for word in words:
    left = words.copy()
    left.remove(word)
    res = []
    for L in word.upper():
        leftCaps = [w[0] for w in left]
        if L in leftCaps:
            i = leftCaps.index(L)
            res.append(left[i])
            left.remove(left[i])

    if len(left) == 0:
        for line in res:
            print(line[0]+': '+line)
