using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        List<string> sentence = Console.ReadLine().Split().ToList();

        List<string> upperCaseLst = new List<string>();
        List<string> lowerCaseLst = new List<string>();

        foreach(string s in sentence)
        {
            if(char.IsUpper(s[0]))
            {
                upperCaseLst.Add(s.ToLower());
            }
            else
            {
                lowerCaseLst.Add(s);
            }
        }

        lowerCaseLst.Reverse();
        List<string> finalLst = new List<string>();
        finalLst.AddRange(upperCaseLst);
        finalLst.AddRange(lowerCaseLst);
        string final = string.Join(" ", finalLst);
        Console.WriteLine($"{char.ToUpper(final[0])}{final.Substring(1)}");
    }
}
