def renversant(nombre) :
    retourner = {"0":"0", "1":"1", "2":"2", "5":"5", "6":"9", "8":"8", "9":"6"}
    n = len(nombre)
    for i in range((n+1)//2) :
        c = nombre[i]
        if not(c in retourner) or nombre[n - i - 1] != retourner[c]:
            return False
    return True

def renverser(nombre, impair) :
    retourner = {"0":"0", "1":"1", "2":"2", "5":"5", "6":"9", "8":"8", "9":"6"}
    if impair :
        return "".join(list(nombre) + [retourner[c] for c in nombre[-2::-1]])
    else :
        return "".join(list(nombre) + [retourner[c] for c in nombre[::-1]])

