import sys

get_bin = lambda x, n: format(x, 'b').zfill(n)


def parseSent(sent,ctx):
	if sent[1] == ">" :
		if ctx[int(sent[0])]: return not parseSent(sent[2:],ctx)
		else: return parseSent(sent[2:],ctx)
	else: return ctx[int(sent[0])]


N = int(input())
L = int(input())
Sentences = []
for i in range(N):
    Sentences.append(input())



for x in range(2**N): # testing all lying posibility
	tabl = [x=="1" for x in get_bin(x,N)] # get the bin representation of is ppl lying ( True, False True etc). True == lie
	if tabl.count(True) !=L:continue # if more liar than there actually are
	allRight = True
	for x in range(len(tabl)):
		if parseSent(Sentences[x],tabl) != (Sentences[x][-1] != "T"): # if th ppl is coherant w/ actual testing
			allRight = False
			break
	if allRight: print(" ".join([str(x) for x in range(len(tabl)) if tabl[x]]));break
	else: print("Not solve for", tabl, file=sys.stderr)
