import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String wordList = in.nextLine();
        String sentence = in.nextLine();

        for(String word : wordList.split(" ")){
            String reg="";
            for(int i=0;i<word.length();i++){
                reg=reg+(i>0?"[^a-zA-Z]*":"")+"["+word.charAt(i)+""+word.toUpperCase().charAt(i)+"]";
            }
            for(int i=0;i<sentence.length();i++){
                if(sentence.substring(i).matches("^"+reg+".*$")){
                    String match = sentence.substring(i).replaceFirst("^("+reg+")(.*$)", "$1");
                    sentence = sentence.replace(match, match.toUpperCase());
                }
            }
        }
        System.out.println(sentence);
    }
}
