#include<stdio.h>


int main()
{
    int h, w, i=-1;
    scanf("%d %d", &h, &w);
	while (++i <= w/2)
	{
		int t=-1;
		while (++t<w/2-i)
			printf(" ");
		printf("/");
		t=-1;
		while (++t<i*2)
			printf(" ");
		printf("\\\n");
	}
	i=-1;
	while (++i < h)
	{
		printf("|");
		int t=-1;
		while (++t < w)
			printf(" ");
		printf("|\n");
	}
	int nw = w;
	while (nw+1 > 0)
	{
		i=-1;
		while (++i <= (w-nw)/2-1)
			printf(" ");
		i=-1;	
		while (++i <= nw+1)
			printf("X");
		printf("\n");
		nw-=2;
	}
    return 0;
}
