#include <iostream>
#include <queue>
using namespace std;
struct Cell
{
    int x;
    int y;
};
int Bfs(int Map[10][10],int sx,int sy)
{
    queue<Cell>BFS;
    BFS.push({sx,sy});
    BFS.push({-1,-1});//in order to increase depth
    bool run=true;
    int depth=0;
    bool visited[10][10];
    for(int i=0;i<10;i++)
    {
        for(int n=0;n<10;n++)
        {
            visited[i][n]=false;
        }
    }
    while(run)
    {
        Cell This=BFS.front();
        BFS.pop();
        if(This.x==-1)
        {
            depth+=1;
            BFS.push({-1,-1});
        }
        else
        {
            if(Map[This.x][This.y]==1)
            {
                run=false;//When path founded run=False
            }
            else
            {
                if(This.x<10){
                if(Map[This.x+1][This.y]!=2&&visited[This.x+1][This.y]==false)
                {
                    BFS.push({This.x+1,This.y});
                    visited[This.x+1][This.y]=true;
                }}
                if(This.x>0){
                if(Map[This.x-1][This.y]!=2&&visited[This.x-1][This.y]==false)
                {
                    BFS.push({This.x-1,This.y});
                    visited[This.x-1][This.y]=true;
                }}
                if(This.y<10){
                if(Map[This.x][This.y+1]!=2&&visited[This.x][This.y+1]==false)
                {
                    BFS.push({This.x,This.y+1});
                    visited[This.x][This.y+1]=true;
                }}
                if(This.y>0){
                if(Map[This.x][This.y-1]!=2&&visited[This.x][This.y-1]==false)
                {
                    BFS.push({This.x,This.y-1});
                    visited[This.x][This.y-1]=true;
                }}
            }
        }
    }
    return depth;
}
int main()
{
    int sx,sy;//starting position of child
    int Map[10][10];//map
    for(int i=0;i<10;i++)
    {
        string k;
        cin>>k;//row input
        for(int j=0;j<10;j++)
        {
            switch(k[j])
            {
                case '.':Map[i][j]=0;break;
                case '#':Map[i][j]=2;break;
                case 'M':Map[i][j]=1;break;
                case 'C':sx=i;sy=j;Map[i][j]=0;break;
            }
        }
    }
    int depth=Bfs(Map,sx,sy);
        cout<<depth*10<<"km";
}
