class Board
{
public:
	static constexpr int numDirections = 8;

	static constexpr uint64_t masks[] = {
		0x7F7F7F7F7F7F7F7FULL,
		0x007F7F7F7F7F7F7FULL,
		0xFFFFFFFFFFFFFFFFULL,
		0x00FEFEFEFEFEFEFEULL,
		0xFEFEFEFEFEFEFEFEULL,
		0xFEFEFEFEFEFEFE00ULL,
		0xFFFFFFFFFFFFFFFFULL,
		0x7F7F7F7F7F7F7F00ULL
	};

	void playMove (unsigned int player, unsigned int move)
	{
		uint64_t disk = 1ULL << move;
		uint64_t capturedDisks = 0;
		uint64_t& myDisks = disks[player];
		uint64_t& opponentDisks = disks[player ^ 1];

		myDisks |= disk;

		for (int i = 0; i < numDirections; i++)
		{
			uint64_t x = disk;
			uint64_t mask = masks[i] & opponentDisks;
			x = shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;

			uint64_t boundingDisk = shift (x, i) & masks[i] & myDisks;
			capturedDisks |= (boundingDisk ? x : 0);
		}
		myDisks ^= capturedDisks;
		opponentDisks ^= capturedDisks;
	}

	uint64_t generateMoves (unsigned int player)
	{
		uint64_t myDisks = disks[player];
		uint64_t opponentDisks = disks[player ^ 1];
		uint64_t emptyCells = ~(myDisks | opponentDisks);
		uint64_t legalMoves = 0;

		for (int i = 0; i < numDirections; i++)
		{
			uint64_t x = myDisks;
		    uint64_t mask = masks[i] & opponentDisks;
			x = shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			x |= shift (x, i) & mask;
			legalMoves |= shift (x, i) & masks[i] & emptyCells;
		}
		return legalMoves;
	}

	uint64_t disks[2] = { 0, 0 };
private:
	uint64_t shift (uint64_t disks, int direction)
	{
		static const uint64_t shifts[] = {
			1, 9, 8, 7, 1, 9, 8, 7
		};

		return (direction < numDirections / 2) ?
			(disks >> shifts[direction]) : (disks << shifts[direction]);
	}

};

