class MonteCarloTreeSearch
{
public:
	class Node
	{
	public:
		Node (Node* parent, unsigned int move, unsigned int playerJustMoved);
		Node () {}
        Node *parent;
		float rewards;
		float visits;
		uint8_t move;
		uint8_t playerJustMoved;
		PoolSlice<Node> children;
		void update (float result, float nbVisits)
		{
			rewards += result;
			visits += nbVisits;
		}
		Node* addChild (unsigned int move, unsigned int playerJustMoved)
		{
			children.emplace_back (this, move, playerJustMoved);
			return &children.back ();
		}
		float value () const { return rewards / visits; }
		float UCTScore (float sqrtLogVisits) const
		{
			float invsqrt = 1.0f /sqrt (visits);
			return ((rewards * invsqrt) + sqrtLogVisits) * invsqrt;
		}
	};

	static constexpr float explorationFactor = 1.0f;
	static constexpr uint32_t maxNodes = 20000000;

	Node* root;

