	unsigned int random = rand (popCount);
	uint64_t randomBit = _pdep_u64 (1ULL << random, availableMoves);
	return __builtin_ctzl (randomBit);
}

int Game::computeResult (unsigned int playerNo) const
{
	unsigned int pros = (unsigned int)__builtin_popcountl (board.disks[playerNo]);
	unsigned int cons = (unsigned int)__builtin_popcountl (board.disks[playerNo ^ 1]);
	if (pros > cons)
		return Win;
	else if (cons > pros)
		return Loss;
	else
		return Draw;
}

class MonteCarloTreeSearch
{
public:
	class Node
	{
	public:
		Node (Node* parent, unsigned int move, unsigned int playerJustMoved);
		Node () {}
        Node *parent;
		float rewards;
		float visits;
		uint8_t move;
		uint8_t playerJustMoved;
		PoolSlice<Node> children;
		void update (float result, float nbVisits)
		{
			rewards += result;
			visits += nbVisits;
		}
		Node* addChild (unsigned int move, unsigned int playerJustMoved)
		{
			children.emplace_back (this, move, playerJustMoved);
			return &children.back ();
		}
		float value () const { return rewards / visits; }
		float UCTScore (float sqrtLogVisits) const
		{
			float invsqrt = 1.0f /sqrt (visits);
			return ((rewards * invsqrt) + sqrtLogVisits) * invsqrt;
		}
	};

	static constexpr float explorationFactor = 1.0f;
	static constexpr uint32_t maxNodes = 20000000;

	Node* root;

	void search (Game& game, const Timer& timer, unsigned int timeOut);
	Node* select (Node*, Game&);
	void expand (Node*, Game&, float&, float&);
	void doRandomPlayout (Game&);
	void backPropagate (Node*, float, float);
	Node* selectBestNode () const;
	Node* findPlayedNode (unsigned int) const;
	void pruneTree (Node* newRoot);
};

MonteCarloTreeSearch::Node* MonteCarloTreeSearch::select (Node* node, Game& game)
{
	while (!node->children.empty ())
	{
		Node* selected = nullptr;
		float bestScore = -std::numeric_limits<float>::max ();

		float sqrtLogVisits = explorationFactor * fastsqrtf (fastlogf (node->visits));
		for (auto& child : node->children)
		{
			float uctScore = child.UCTScore (sqrtLogVisits);
			if (uctScore > bestScore)
			{
				bestScore = uctScore;
				selected = &child;
			}
		}
		if (!selected)
			break;
		else
			node = selected;
		if (node->move == Pass)
			game.skipMove ();
		else
			game.playMove (node->move);
	}
	return node;
}

MonteCarloTreeSearch::Node::Node (Node* parent, unsigned int move, unsigned int playerJustMoved)
	: parent(parent), visits (0), rewards (0),
	move (move), playerJustMoved (playerJustMoved)
{
}

void MonteCarloTreeSearch::doRandomPlayout (Game& game)
{
	while (true)
	{
		uint64_t availableMoves = game.generateMoves ();
		uint64_t nbMoves = __builtin_popcountl (availableMoves);
		if (!nbMoves)
		{
			game.skipMove ();
			availableMoves = game.generateMoves ();
			nbMoves = __builtin_popcountl (availableMoves);
			if (!nbMoves)
				break;
		}
		game.playMove (game.selectRandomMove (availableMoves));
	}
}

void MonteCarloTreeSearch::backPropagate (Node* node, float result, float visits)
{
	while (node != nullptr)
	{
		node->update (result, visits);
		result = -result;
		node = node->parent;
	}
}

void MonteCarloTreeSearch::expand (Node* node, Game& game, float& rewards, float& visits)
{
    uint64_t availableMoves = game.generateMoves ();
    unsigned int nbMoves = (unsigned int)__builtin_popcountl (availableMoves);
    if (nbMoves == 0)
    {
        game.skipMove ();
        node->children.reserve (1);
        Node* child = node->addChild (Pass, game.playerJustMoved);
        child->visits = 1;
        visits++;

        if (node->move != Pass)
            doRandomPlayout (game);
        child->rewards = (float)game.computeResult (child->playerJustMoved);
        rewards -= child->rewards;
    }
    else
    {
        node->children.reserve (nbMoves);
        while (availableMoves)
        {
            unsigned int move = __builtin_ctzl (availableMoves);
            availableMoves ^= 1ULL << move;
            Game childPlayout = game;
            childPlayout.playMove (move);
            Node* child = node->addChild (move, childPlayout.playerJustMoved);
            visits++;
            doRandomPlayout (childPlayout);
            child->rewards = (float)childPlayout.computeResult (child->playerJustMoved);
            child->visits = 1;
            rewards -= child->rewards;
        }
    }
}

void MonteCarloTreeSearch::search (Game& game, const Timer& timer, unsigned int timeOut)
{
	while (!timer.isElapsed (timeOut))
	{
		Game playout = game;
		Node* node = select (root, playout);
		float rewards = 0;
		float visits = 0;

		expand (node, playout, rewards, visits);
		backPropagate (node, rewards, visits);
	}
}

MonteCarloTreeSearch::Node* MonteCarloTreeSearch::selectBestNode () const
{
	Node* node = &*std::max_element (root->children.begin (), root->children.end (), [] (const Node& a, const Node& b)
	{
		return a.value () < b.value ();
	});
	return node;
}

MonteCarloTreeSearch::Node* MonteCarloTreeSearch::findPlayedNode (unsigned int move) const
{
	for (auto& node : root->children)
	{
		if (node.move == move)
			return &node;
	}
	return nullptr;
}

void MonteCarloTreeSearch::pruneTree (Node* newRoot)
{
	root = newRoot;
    root->parent = nullptr;
}

