class Agent
{
public:
	void start ();
	void read ();
	void think ();
	void write ();
private:
	Game game;
	MonteCarloTreeSearch mcts;
	Timer timer;
	int turn = 0;
	int myId; // id of your player.
};

void Agent::start ()
{
	cin >> myId; cin.ignore ();
	int height; // number of rows.
	cin >> height; cin.ignore ();

	if (myId == 0)
	{
		game.ply = 0;
		game.playerJustMoved = 1;
	}
	else
	{
		game.ply = 1;
		game.playerJustMoved = 0;
	}
	PoolSlice<MonteCarloTreeSearch::Node>::_index = 1;
}

void Agent::read ()
{
	turn++;
	Board current;

	for (int i = 0; i < 8; i++)
	{
		string line; // rows from top to bottom (viewer perspective).
		cin >> line; cin.ignore ();
		for (int j = 0; j < 8; j++)
			if (line[j] == '0')
				current.disks[0] |= 1ULL << (i * 8 + j);
			else if (line[j] == '1')
				current.disks[1] |= 1ULL << (i * 8 + j);
			else
				;
	}
	string moves;
	if (turn > 1)
	{
		cin >> moves; cin.ignore ();
	}
	int actionCount; // number of legal actions for this turn.
	cin >> actionCount; cin.ignore ();
	for (int i = 0; i < actionCount; i++)
	{
		string action; // the action
		cin >> action; cin.ignore ();
	}

	if (turn == 1)
	{
		game.board = current;
		mcts.root = new (&PoolSlice<MonteCarloTreeSearch::Node>::_pool[0]) MonteCarloTreeSearch::Node (nullptr, Pass, game.playerJustMoved);
	}
	else
	{
        auto opponentMoves = split(moves, ';');
        if (opponentMoves[0] == "pass")
		{
			game.skipMove ();
			MonteCarloTreeSearch::Node* node = mcts.findPlayedNode (Pass);
			mcts.pruneTree (node);
		}
		else
		{
            int move = stringToMove(opponentMoves[0]);
			game.playMove (move);
			MonteCarloTreeSearch::Node* node = mcts.findPlayedNode (move);
			mcts.pruneTree (node);
            for (int i = 1; i < opponentMoves.size(); i++)
            {
                node = mcts.findPlayedNode (Pass);
                mcts.pruneTree (node);
                game.skipMove();
                int move = stringToMove(opponentMoves[i]);
                game.playMove (move);
                node = mcts.findPlayedNode (move);
                mcts.pruneTree (node);
            }
		}
	}
}

void Agent::think ()
{
	timer.start ();
	mcts.search (game, timer, turn == 1 ? 2000 : 150);
}

void Agent::write ()
{
	MonteCarloTreeSearch::Node* bestNode;
	bestNode = mcts.selectBestNode ();
	mcts.pruneTree (bestNode);
	game.playMove (bestNode->move);

	cout << "EXPERT " << Move (bestNode->move) << endl;
}

