#include <stdio.h>
#include <time.h>
int main()
{
	//time_t rawtime1, rawtime2; time(&rawtime1);

	int N, X, R; scanf("%d %d", &N, &X); //fprintf(stderr, "N = %d %d\n", N, X);
	int *pp = (int*)malloc((N + 1) * sizeof(int)); //number of floors covered with i drops having 1 egg less
	int *pi = (int*)malloc((N + 1) * sizeof(int)); //number of floors covered with i drops

	pp[0] = pi[0] = 0;
	for (int e = 1; e <= X; e++) {
		for (int i = 1; i <= N; i++) {
		    pi[i] = 1 + pi[i-1] + pp[i-1];
		    if (pi[i]>=N) {R = i; break;} // we covered enough floors;
		}
		for (int i = 1; i <= R; i++) { pp[i] = pi[i];}
	}
	printf("%d\n", R);

	//time(&rawtime2); //fprintf(stderr,"\ncalc time = %d s\n", rawtime2 - rawtime1);
	free(pp);
	free(pi);
}
