using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;

public class Program
{
	public static void Main()
	{
		string statement = Console.ReadLine();
		var inputRay = statement.Split(' ');
		string theMonth = inputRay[0];
		int theDayOfMonth = Int16.Parse(inputRay[1]);
		string theDayOfWeek = inputRay[4];
		var WeekRay = "Monday Tuesday Wednesday Thursday Friday Saturday Sunday".Split(' ');
		int daysInMonth = 31;
		bool is30dayMonth = "September April June November".Contains(theMonth);
		if (is30dayMonth) daysInMonth = 30;
		int theDayIndex = Array.IndexOf(WeekRay,theDayOfWeek);

		int diff = theDayIndex - theDayOfMonth;
		while (diff < 1) diff += 7;
		for (int dayOfMonth = 1 ; dayOfMonth <= daysInMonth ; dayOfMonth++)
		{
			int offset = dayOfMonth + diff;
			int weekIndex = offset%7;

			if (weekIndex < 5)
			{
				string st = WeekRay[weekIndex] + ", " + theMonth + " " + dayOfMonth; 	
				Console.WriteLine(st);
			}
		}
	}
}

