#include <stdlib.h>
#include <stdio.h>
#include <string.h>
#include <stdbool.h>

int main()
{
    float m;
    scanf("%f", &m);
    float v;
    scanf("%f", &v);

    // Let phi denote volume concentration. Then
    //    0.79 * (v * phi) + 1 * (v * (1 - phi)) = m
    // ->           v * (0.79 * phi + (1 - phi)) = m
    // ->                   v * (1 - 0.21 * phi) = m
    // ->                         1 - 0.21 * phi = m / v
    // ->                              1 - m / v = 0.21 * phi
    // ->                     (1 - m / v) / 0.21 = phi
    const float phi = (1 - m / v) / 0.21;

    printf("%.0f%%\n", phi * 100);

    return 0;
}
