using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Globalization;

public class Program
{
	public static void Main()
	{
		List<String> InputList = new  List<String>();
		for (int i = 0 ; i < 3 ; i++) 
		{
            string INPUT = Console.ReadLine();
			InputList.Add(INPUT);
		}
		List<string> AllPossibleStubstringsList = GetAllPossibleStubstrings(InputList[0]);
		string commonWord = GetCommonWord(InputList, AllPossibleStubstringsList);
		string str0 = InputList[0].Replace(commonWord,"").Trim();
		string str1 = InputList[1].Replace(commonWord,"").Trim();
		string str2 = InputList[2].Replace(commonWord,"").Trim();
		string slash = " / ";

		string strA = str0 + slash + str1 + slash + str2;
        Console.WriteLine(strA);  
		string strB = "Answer: " + commonWord;
		Console.WriteLine(strB);
	}

	public static List<string> GetAllPossibleStubstrings(string aFullWord)
	{
        // find all substrings of the first word/phrase that are over 1 letter in length 
        // and don't contain a space (i.e., aren't from 2 separate words in a phrase)
		List<String> AllSubstringsList = new List<String>();

		int LEN = aFullWord.Length;
		for (int start = 0; start < LEN; start++) {
			for (int end = 0; end < LEN; end++) {
				int len = end - start + 1; //length of substring
				if (len > 1)
				{
					string aSubString = aFullWord.Substring(start, len);
					if (aSubString.Contains(" ") == false) 
					{
						AllSubstringsList.Add(aSubString); 
					}
				}
			}
		}		
		return AllSubstringsList;
	}

	public static string GetCommonWord(List<String> FullWordsList, List<String> AllSubstrings)
	{
		List<String> CommonWordsList = new List<String>();

		for (int i = 0; i < AllSubstrings.Count; i++) 
		{
            string thisSubString = AllSubstrings[i];
			bool bool0 = FullWordsList[0].Contains(thisSubString);
			bool bool1 = FullWordsList[1].Contains(thisSubString);
			bool bool2 = FullWordsList[2].Contains(thisSubString);
			bool bool3 = bool0 && bool1 && bool2;
			if (bool3) //if that substring is in all 3 word/phrases
			{
				CommonWordsList.Add(thisSubString);
			}
		}

        //https://stackoverflow.com/questions/7975935/is-there-a-linq-function-for-getting-the-longest-string-in-a-list-of-strings
        string ans = CommonWordsList.Aggregate("", (max, cur) => max.Length > cur.Length ? max : cur);
        return ans;
	}
}
