using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Globalization;

public class Program
{
	public static void Main()
	{
		string Excerpt = Console.ReadLine();
		var ExcerptWords = Excerpt.Split(' ');

		string noGs = "";

		foreach (string word in ExcerptWords)
		{
			bool hasG = word.ToLower().Contains('g');
			if (hasG)
			{
				if (noGs != "")
				{	
					Console.WriteLine(noGs.Trim());
					noGs = "";
				}
				DisplayGword(word);					
			}
			else
			{
				noGs = noGs + word + " ";
			}
		}
		if (noGs.Trim() != "") Console.WriteLine(noGs.Trim());
	}

	public static void DisplayGword(string gword)
	{
		int len = gword.Length;

		string topBorder = new string ('v', len+4);
		string bottomBorder = new string ('^', len+4);
		Console.WriteLine(topBorder);
		Console.WriteLine("> " + gword.Trim() + " <");//stars);
		Console.WriteLine(bottomBorder);
	}
}
