function getAlphabeticPosition(char) {
    return char.toUpperCase().charCodeAt(0) - 64
}

const input = readline()

let sum = 0

// Add the lowercase letters to sum
input.replace(/[a-z]/g, char => {
    sum += getAlphabeticPosition(char)
})

// Add the uppercase letter to sum
input.replace(/[A-Z]/g, char => {
    sum += getAlphabeticPosition(char) * 2
})

// Split sum into an array containing the digits
sum = sum.toString().split("")

// Append zeros until the sum reaches 6 digits
while(sum.length < 6) {
    sum.push("0")
}

// Clip all overflowing digits
sum.length = 6

// Join the digits to a string
sum = sum.join("")

console.log(sum)
