using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;
/*
Given the upper right quadrant of a tile pattern, create the entire tile pattern. 
And print 4 of that full-tile pattern in a 2x2 square

RULES:
~Each tile is square; therefore each quarter-tile is square;
~The input square shows the top left quadrant
~if the input has a side-size z, the output will have a side-size of (2z) 
~ultimate Output needs to have a top (-) and a side (|) border added to it and t (+) for the 4 corners
~Show a 2 x 2 square, separating them by same characters as shown above
*/

public class Program
{
	public static string flipPatternBOOK = "bd qp".Replace(" ","");  // bookFlip
	public static string flipPatternDOWN = "bp dq".Replace(" ","");  // downFlip
	public static string flipPatternDIAG = "bq dp".Replace(" ","");  // diag     
	public static char[,] FINALRAY;

	public static void Main()
	{
        int quarterSize = int.Parse(Console.ReadLine());
        int sz = quarterSize;

		List<String> aQtr = new List<String>();

        for (int i = 0 ; i < sz ; i++)
        {
            string ROW = Console.ReadLine();
            aQtr.Add(ROW);
        }

		SquareModel aTile = new SquareModel(aQtr);
		TileFloor tf = new TileFloor(aTile);
		tf.PrintTileFloor();
	}

	public class SquareModel
	{
		public List<string> Lines;
		public char[,] TopLeftQtr;
		public char[,] FullSquare;
		public int QtrSize;
		public int FullSize;

		public SquareModel(List<string> lines)
		{
			Lines = lines;
			QtrSize = lines[0].Length;
			FullSize = 2 * QtrSize;

			TopLeftQtr = new char[QtrSize, QtrSize];	
			FullSquare = new char[FullSize, FullSize];

			PopulateQtrSquare();
			InitializeFullSquare();
			PopulateFullSquare();
		}

		public void PopulateQtrSquare()
		{
			for (int lin = 0 ; lin < QtrSize ; lin++)
			{
				for (int pos = 0 ; pos < QtrSize ; pos++)
				{
					TopLeftQtr[lin,pos] = Lines[lin][pos];
				}
			}
		}

		public void InitializeFullSquare()
		{
			for (int lin = 0 ; lin < FullSize; lin++)
			{
				for (int pos = 0 ; pos < FullSize ; pos++)
				{
					FullSquare[lin, pos] =  ' ';
				}
			}
		}

		public void PopulateSingleQtr(int whichQtr)
		{
			int biFull = FullSize - 1;                 //biggest Index Of Full
			for (int lin = 0 ; lin < QtrSize ; lin++)
			{
				for (int pos = 0 ; pos < QtrSize ; pos++)
				{
					char ch1 = TopLeftQtr[lin,pos];

					if (whichQtr == 1)
					{
						FullSquare[lin, pos] = ch1;
					}

					if (whichQtr == 2) // bookFlip
					{
						char ch2 = FindMirror(ch1, flipPatternBOOK);
						ch2 = SwapSlashes(ch2);
						FullSquare[lin, biFull - pos] = ch2;	
					}

					if (whichQtr == 3) // downFlip
					{
						char ch2 = FindMirror(ch1, flipPatternDOWN);
						ch2 = SwapSlashes(ch2);
						FullSquare[biFull - lin, pos] = ch2;
					}

					if (whichQtr == 4) //diag
					{
						char ch2 = FindMirror(ch1, flipPatternDIAG);
						FullSquare[biFull - lin, biFull-pos] = ch2;
					}
					/*
					assumes quardrants are like
					12
					34
					*/
				}
			}
		}

		public static char SwapSlashes(char ch)
		{
			if (ch == '\\') return '/';	
			if (ch == '/') return '\\';	
			return ch;
		}

		public void PopulateFullSquare()             
		{
			PopulateSingleQtr(1); PopulateSingleQtr(2); PopulateSingleQtr(3); PopulateSingleQtr(4);
		}

		public char FindMirror(char ch1, string strOptions)
		{
			char[] OptionsRay = strOptions.ToCharArray();	
			char ch2 = ch1;

			int index = Array.IndexOf(OptionsRay, ch1);
			if (index != -1)
			{
				if (index % 2 == 0) ch2 = OptionsRay[index+1];
				if (index % 2 == 1) ch2 = OptionsRay[index-1];
			}
			return ch2;
		}
	}

	public class TileFloor
	{
		public SquareModel Tile;
		public int TileSiz;
		public int TileSizPLUS;
		public int FloorSiz;

		public TileFloor(SquareModel aTile)
		{
			Tile = aTile;
			TileSiz = Tile.FullSize;
			TileSizPLUS = TileSiz + 1; //allows for a left and top border
			FloorSiz = 2 * TileSizPLUS + 1;
			FINALRAY = new char[FloorSiz,FloorSiz];
			InitializeTileFloor();

			for (int lin = 0 ; lin < FloorSiz; lin++)
			{
				for (int pos = 0 ; pos < FloorSiz ; pos++)
				{
					int remLin = lin % TileSizPLUS;
					int remPos = pos % TileSizPLUS;

					if (remLin == 0)
					{
						FINALRAY[lin,pos] = '-';
					}
					if (remPos == 0)
					{
						FINALRAY[lin,pos] = '|';
					}

					if (remLin + remPos == 0)
					{
						FINALRAY[lin,pos] = '+';
					}
				}
			}

			PopulateFloor(0,0);
			PopulateFloor(0,1);
			PopulateFloor(1,0);
			PopulateFloor(1,1);
		}

		public void InitializeTileFloor()
		{
			for (int lin = 0 ; lin < FloorSiz; lin++)
			{
				for (int pos = 0 ; pos < FloorSiz ; pos++)
				{
					FINALRAY[lin, pos] =  ' ';
				}
			}
		}

		public void PrintTileFloor()
		{
			for (int lin = 0 ; lin < FloorSiz ; lin++)
			{
				for (int pos = 0 ; pos < FloorSiz  ; pos++)
				{
					char ch = FINALRAY[lin,pos];
					Console.Write(ch);
				}
				Console.WriteLine();
			}
		}
		public void PopulateFloor(int offsetLin, int offsetPos)
		{
			for (int lin = 0 ; lin < TileSiz; lin++)
			{
				for (int pos = 0 ; pos < TileSiz ; pos++)
				{
					int LIN = lin + 1 + (offsetLin * TileSizPLUS);
					int POS = pos + 1 + (offsetPos * TileSizPLUS);
					FINALRAY[LIN, POS] = Tile.FullSquare[lin, pos]; 
				}
			}
		}	
	}
}
