var P = parseInt(readline());
var players = [];
for (var i = 0; i < P; i++) {
    var input = readline().split(' ');
    players.push({name: input[0], position: parseInt(input[1]), inJail: false, jailTurns: 0});
}
var D = parseInt(readline());
var dices = [];
for (var i = 0; i < D; i++) {
    dices.push(readline());
    dices[i] = dices[i].split(' ');
    for (var j = 0; j < dices[i].length; j++) {
        dices[i][j] = parseInt(dices[i][j]);
    }
}
var board = [];
for (var i = 0; i < 40; i++) {
    board.push(readline());
}
// printErr("players:", players);
// printErr("dices:", dices);
// printErr("board:", board);

var turnPlayer = 0;
var playedInARow = 0;
for (var i = 0; i < dices.length; i++) {
    var sum = 0;
    var stock;
    var isDouble = true;
    var futurePos;
    for (var j = 0; j < dices[i].length; j++) {
        (j === 0) ? (stock = dices[i][j]) : ((stock != dices[i][j]) ? (isDouble = false) : (0));
        sum += dices[i][j];
    }
    // printErr("sum:", sum);
    // printErr("isDouble:", isDouble);
    // printErr("Turn:", players[turnPlayer].name)

    futurePos = players[turnPlayer].position + sum;
    while (futurePos >= 40) {
        futurePos -= 40;
    }
    if (players[turnPlayer].inJail) {
        if (!isDouble) {
            players[turnPlayer].jailTurns += 1;
        }
        if (players[turnPlayer].jailTurns == 3 || isDouble) {
            players[turnPlayer].position = futurePos;
            players[turnPlayer].jailTurns = 0;
            players[turnPlayer].inJail = false;
        }
        turnPlayer += 1;
    } else {
        if (isDouble) {
            playedInARow += 1;
        }
        if (playedInARow == 3) {
            players[turnPlayer].position = board.indexOf("Visit Only / In Jail");
            players[turnPlayer].inJail = true;
        } else if (board[futurePos] == "Go To Jail") {
            players[turnPlayer].position = board.indexOf("Visit Only / In Jail");
            players[turnPlayer].inJail = true;
        } else {
            players[turnPlayer].position = futurePos;
        }
        if (!isDouble || players[turnPlayer].inJail) {
            playedInARow = 0;
            turnPlayer += 1;
        }
    }
    if (turnPlayer > players.length - 1) {
        turnPlayer = 0;
    }
}

for (var i = 0; i < players.length; i++) {
    print(players[i].name + ' ' + players[i].position);
}
