import math 
def main():
    n = int(input())
    print(pi_digit(n))

def fraction_mod1(f):
    # modf returns a tuple of (fraction, whole), and force positive remainder
    return math.modf(f + 256)[0]


def pi_sum(n, offset):
    s = 0.0
    k = offset
    for i in range(n+1):
        s = fraction_mod1(s + pow(16, n - i, k) / k)
        k += 8

    num = 1 / 16.0
    while (num / k) > 0.0:
        s += num / k
        num /= 16
        k += 8

    return fraction_mod1(s)

def pi_digit(n):
    s = 0.0

    for mult, offset in [(4,1), (-2,4), (-1, 5), (-1,6)]:
        s += mult * pi_sum(n - 1, offset)

    return int(16*fraction_mod1(s))

if __name__ == '__main__':
    main()
